import { of } from 'rxjs';
export class SimpleLoadingStrategy {
  constructor() {
    this.routesToPreload = [];
  }

  preload(route, load) {
    if (route.data && route.data.preload) {
      return load();
    }

    return of(null);
  }

  preloadRoute(path) {
    return new Promise(resolve => {
      if (this.routesToPreload && this.routesToPreload.length > 0) {
        const routeToPreload = this.routesToPreload.find(filterRouteToPreload => filterRouteToPreload.routePath === path);

        if (routeToPreload) {
          routeToPreload.load();
        }
      }

      resolve();
    });
  }

}
